$(document).ready(function(){$("a[rel='img']").colorbox({maxWidth:"98%",maxHeight:"98%"});$("[rel='tooltip']").tooltip();$("[rel='popover']").popover();$(".alert").alert();$(".affix").affix();if(/Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent)){$(".selectpicker").selectpicker("mobile")}else{$(".selectpicker").selectpicker()}$(window).scroll(function(){if($(this).scrollTop()>100){$("a#move_top").fadeIn()}else{$("a#move_top").fadeOut(400)}});$("a#move_top").click(function(){$("html, body").animate({scrollTop:0},"500","swing");return false})});